import React, { useState } from "react";
import "antd/dist/antd.css";
import { Button, Typography, Table, Input } from "antd";
import { useQuery, gql } from '@apollo/client';
import { Address } from "../components";
import GraphiQL from 'graphiql';
import 'graphiql/graphiql.min.css';
import fetch from 'isomorphic-fetch';



function Subgraph(props) {
  state = {
    text: 'something'
  }









  const [newPurpose, setNewPurpose] = useState("loading...");




  return (
      <>





          <div style={{width:780, margin: "auto", paddingBottom:64}}></div>

            <h1>After deliberation we the jury find the defendant</h1>




          <div style={{width:780, margin: "auto", paddingBottom:64}}>

            <div style={{margin:32, textAlign:'middle'}}>


              <Button danger block size={"large"} onClick={()=>{
                console.log("Final Verdict: ",newPurpose)
                /* look how you call setPurpose on your contract: */
                props.tx( props.writeContracts.YourContract.setPurpose(newPurpose) )
              }}>Final Verdict</Button>
            </div>



          </div>

          <div style={{padding:64}}>

          </div>
      </>
  );
}

export default Subgraph;
