/* eslint-disable jsx-a11y/accessible-emoji */

import React, { useState } from "react";
import "antd/dist/antd.css";
import { Button, Typography, Table, Input } from "antd";
import { useQuery, gql } from '@apollo/client';
import { Address } from "../components";
import GraphiQL from 'graphiql';
import 'graphiql/graphiql.min.css';
import fetch from 'isomorphic-fetch';

  const highlight = { marginLeft: 4, marginRight: 8, /*backgroundColor: "#f9f9f9",*/ padding: 4, borderRadius: 4, fontWeight: "bolder" }

  export default function Subgraph({count, purpose, setGuilty, setPurposeEvents, address, mainnetProvider, userProvider, localProvider, yourLocalBalance, price, tx, readContracts, writeContracts }) {



  const [newPurpose, setNewPurpose] = useState("loading...");


  const deployWarning = (
    <div style={{marginTop:8,padding:8}}>{"Warning: ðŸ¤” Have you deployed your subgraph yet?"}</div>
  )

  return (
      <>



          <div style={{ width:600, margin: "auto", marginTop:32, paddingBottom:32 }}>
        <h2>Events:</h2>
        <List
          bordered
          dataSource={setPurposeEvents}

          renderItem={item => {
            return (
              <List.Item key={item.time+"_"+item.id+"_"+item.addr+"_"+item.count+"_"+item.verdict+"_"+item.setnotguilty+"_"+item.setguilty+"_"}>

               <font color='grey' fontSize={25}>Juror Address: </font><Address
                    address={item[2]}
                    ensProvider={mainnetProvider}
                    fontSize={16}
                    font={green}
                  /> <font color='grey'>Full Name: </font> 
                {item[1]}

              <div></div>
                <font color='grey'>Decision:</font> 

                <font color=''> {item[6]} </font>

              </List.Item>



            );
          }}
        />
      </div>


          <div style={{width:780, margin: "auto", paddingBottom:64}}></div>

            <h1>After deliberation we the jury find the defendant</h1>




          <div style={{width:780, margin: "auto", paddingBottom:64}}>

            <div style={{margin:32, textAlign:'middle'}}>


              <Button danger block size={"large"} onClick={()=>{
                console.log("Final Verdict: ",newPurpose)
                /* look how you call setPurpose on your contract: */
                tx(writeContracts.YourContract.Verdict() )
              }}>Final Verdict</Button>
            </div>



          </div>

          <div style={{padding:64}}>

          </div>
      </>
  );
}
